package operadores;

public class IncrementoDecremento {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		//Incremento ++
		//Decremento --
		int x = 9;
		x++; //Suma 1 a X
		++x; //Suma 1 a x
		System.out.println("x = " + x);
		
		int y = 10;
		int z = y++;
		int w = ++y;
		
		System.out.println("z = " + z);
		System.out.println("y = " + y);
		
		
		
		
		
		
	}

}
